template.script:
dd.ready(function() {
    dd.runtime.permission.requestAuthCode({
        corpId: "",
        onSuccess: function(result) {
            var code = result.code;
            window.location.href = window.location.href+"&code="+code;
        },
        onFail: function(err){
            alert(JSON.stringify(err));
        }
    });
});
